import Heading from "../layout/Heading";
import { useState, useEffect } from "react";
import { BASE_URL, MESSAGE_PATH } from "../../constants/api";
import useAxios from "../../hooks/useAxios";
import MessageItem from "./MessageItem";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Spinner from "react-bootstrap/Spinner";
import Alert from "react-bootstrap/Alert";
import Table from "react-bootstrap/Table";

export default function AdminMessagesPage() {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const url = BASE_URL + MESSAGE_PATH;
  document.title = `Holidaze | Admin`;
  const http = useAxios();


  useEffect(() => {
    async function fetchMessages() {
      try {
        const response = await http.get(url);
        console.log("response", response);
        setMessages(response.data);
      } catch (error) {
        console.log(error);
        setError("An error occurred:" + error.toString());
      } finally {
        setLoading(false);
      }
    }
    fetchMessages();

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [url]);

  if (loading) {
    return (
      <div className="d-flex justify-content-center m-5">
        <Spinner animation="border" />
      </div >
    );
  }

  if (error) {
    return <Alert variant="danger">An error occurred: {error}</Alert>;
  }

  return (
    <Container>
      <Heading content="Inbox" />
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Sender</th>
            <th>Email</th>
            <th>Message</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            {messages.map((messageSender) => {
              const { id, name } = messageSender;
              return (
                <>
                  <td key={id}>{name}</td>
                </>
              )
            })}
          </tr>
          <tr>
            <td>2</td>
            <td>Jacob</td>
            <td>Thornton</td>
            <td>@fat</td>
          </tr>
          <tr>
            <td>3</td>
            <td colSpan={2}>Larry the Bird</td>
            <td>@twitter</td>
          </tr>
        </tbody>
      </Table>
      <Row className="messages-container">
        {messages.map((messageItem) => {
          const { id, name, email, message } = messageItem;
          return (
            <>
              <MessageItem key={id} id={id} name={name} email={email} message={message} />
            </>
          )
        })}
      </Row>
    </Container>
  );

}
