import { useState, useEffect } from "react";
import { BASE_URL, ACCOMMODATION_PATH } from "../../constants/api";
import Form from "react-bootstrap/Form";

export default function SearchAccommodations() {
  const [accommodations, setAccommodations] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const url = BASE_URL + ACCOMMODATION_PATH;
  document.title = `Holidaze | Accommodations`;

  useEffect(() => {
    async function fetchAccommodations() {
      try {
        const response = await fetch(url);
        console.log(response);
        if (response.ok) {
          const json = await response.json();
          console.log(json);
          setAccommodations(json);
        } else {
          setError("An error occurred");
        }

      } catch (error) {
        setError(error.toString());
      } finally {
        setLoading(false);
      }

      const results = accommodations.filter(accommodation =>
        accommodation.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setSearchResults(results);

    }
    fetchAccommodations();

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  if (loading) {
    return <div>Loading...</div >
  }
  if (error) {
    return console.log(error);
  }

  const handleSearch = event => {
    setSearchTerm(event.target.value);
  };

  // useEffect(() => {
  //   const results = accommodations.filter(accommodation =>
  //     accommodation.toLowerCase().includes(searchTerm.toLowerCase())
  //   );
  //   setSearchResults(results);
  // }, [searchTerm]);

  return (
    <>
      <Form>
        <Form.Group className="mb-3" >
          <input className="form-control" type="text" placeholder="Search for a place to stay..." value={searchTerm} onChange={handleSearch} />
        </Form.Group>
      </Form>
      <ul>
        {searchResults.map(item => (
          <li>{item.name}</li>
        ))}
      </ul>
    </>
  )
}
