import { useState, useEffect } from "react";
import { BASE_URL, ACCOMMODATION_PATH } from "../../constants/api";
import { Link } from "react-router-dom";
import Form from "react-bootstrap/Form";

export default function SearchAccommodations() {
  const [accommodations, setAccommodations] = useState([]);
  const [searchInput, setSearchInput] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [display, setDisplay] = useState(true);

  const url = BASE_URL + ACCOMMODATION_PATH;

  useEffect(() => {
    async function fetchAccommodations() {
      try {
        const response = await fetch(url);
        if (response.ok) {
          const json = await response.json();
          setAccommodations(json);
        } else {
          setError("An error occurred");
        }
      } catch (error) {
        setError(error.toString());
      } finally {
        setLoading(false);
      }
    }
    fetchAccommodations();
  }, [url]);

  if (loading) {
    return <div>Loading...</div >
  }
  if (error) {
    return console.log(error);
  }

  const handleSearch = event => {
    setSearchInput(event.target.value);
    setDisplay(false);
  };

  const results = !searchInput
    ? accommodations
    : accommodations.filter(accommodation =>
      accommodation.name.toLowerCase().includes(searchInput.toLocaleLowerCase())
    );


  return (
    <>
      <Form>
        <Form.Group className="mb-3" >
          <input className="form-control" type="text" placeholder="Search for a place to stay..." value={searchInput} onChange={handleSearch} />
        </Form.Group>
      </Form>
      <div className="search-dropdown">
        {results && results.map((item) => {
          return (
            <div className="search-dropdown__item" key={item.id} style={{ backgroundColor: "white", display: display ? "none" : "block" }}>
              <Link className="search-dropdown__item--link" to={`detail/${item.id}`}>{item.name}</Link>
            </div>
          );
        })}
      </div>
    </>
  )
}
