import Heading from "../layout/Heading";
import { useState, useEffect } from "react";
import { BASE_URL, MESSAGE_PATH } from "../../constants/api";
import useAxios from "../../hooks/useAxios";
import Message from "./Message";
import Row from "react-bootstrap/Row";
import Spinner from "react-bootstrap/Spinner";
import Alert from "react-bootstrap/Alert";

function AdminMessagesPage() {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const url = BASE_URL + MESSAGE_PATH;
  const http = useAxios();

  useEffect(() => {
    async function fetchMessages() {
      try {
        const response = await http.get(url);
        console.log("response", response);
        setMessages(response.data);
      } catch (error) {
        console.log(error);
        setError("An error occurred:" + error.toString());
      } finally {
        setLoading(false);
      }
    }
    fetchMessages();

  }, []);

  if (loading) {
    return (
      <div className="d-flex justify-content-center m-5">
        <Spinner animation="border" />
      </div >
    );
  }

  if (error) {
    return <Alert variant="danger">An error occurred: {error}</Alert>;
  }

  return (
    <>
      <Heading content="Admin Messages" />
      <Row className="messages-container">
        {messages.map((props) => {
          const { id, name, email, message } = props;
          return <Message key={id} id={id} name={name} email={email} message={message} />
        })}
      </Row>
    </>
  );

}



export default AdminMessagesPage;
