import { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import { BASE_URL } from "../../constants/api";
import Heading from "../layout/Heading";
import Card from "react-bootstrap/Card";
import Spinner from "react-bootstrap/Spinner";
import Alert from "react-bootstrap/Alert";

export default function DetailsPage() {
  const [product, setProduct] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  let { id } = useParams();

  const url = BASE_URL + `accommodations/${id}`;

  useEffect(function () {
    async function getData() {
      try {
        const response = await axios.get(url);
        console.log("response", response);
        setProduct(response.data);
      } catch (error) {
        console.log(error);
        setError(error.toString());
      } finally {
        setLoading(false);
      }
    }

    getData();

  }, [url]);

  if (loading) return (
    <div className="d-flex justify-content-center m-5">
      <Spinner animation="border" />
    </div >
  );;

  if (error) return <Alert variant="danger">An error occurred: {error}</Alert>;

  const description = { __html: `${product.description}` };
  document.title = `Holidaze | ${product.name}`;
  return (
    <>
      <Heading content="Page Details" />
      <div className="breadcrumb">
        <Link to={`/accommodations`}>Back</Link>
      </div>

      <Card className="details-card" >
        <Card.Img variant="top" src={product.images.map(img => img.url)} />


        <Card.Body>
          <Card.Title>{product.name}</Card.Title>
          <Card.Text>
            Price: {product.price}
          </Card.Text>
          <div className="card-text" dangerouslySetInnerHTML={description}></div>
        </Card.Body>
      </Card>
    </>
  );
}
