import { useState, useEffect } from "react";
import { BASE_URL, ACCOMMODATION_PATH } from "../../constants/api";
import Form from "react-bootstrap/Form";

export default function SearchAccommodations() {
  const [accommodations, setAccommodations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const url = BASE_URL + ACCOMMODATION_PATH;
  document.title = `Holidaze | Accommodations`;

  useEffect(() => {
    async function fetchAccommodations() {
      try {
        const response = await fetch(url);
        console.log(response);
        if (response.ok) {
          const json = await response.json();
          console.log(json);
          setAccommodations(json);
        } else {
          setError("An error occurred");
        }

      } catch (error) {
        setError(error.toString());
      } finally {
        setLoading(false);
      }

    }
    fetchAccommodations();

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  if (loading) {
    return <div>Loading...</div >
  }

  if (error) {
    return console.log(error);
  }
  return (
    <Form>
      <Form.Group className="mb-3" >
        <input className="form-control" type="text" placeholder="Search for a place to stay..." />
      </Form.Group>
    </Form>
  )
}
