import { useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import FormError from "../common/FormError";
import Form from "react-bootstrap/Form";

const validationSchema = yup.object().shape({
  firstName: yup.string().required("Please enter your first name").min(3, "First name must be at least 3 characters"),
  lastName: yup.string().required("Please enter your last name").min(4, "Last name must be at least 4 characters"),
  email: yup.string().required("Please enter an email address").email("Please enter a valid email address"),
  message: yup.string().required("Please enter your message").min(10, "The message must be at least 10 characters"),
  subject: yup.string().required("Please select the subject"),
});

export default function ContactForm() {
  const { register, handleSubmit, reset, formState: { errors, isSubmitSuccessful } } = useForm({
    resolver: yupResolver(validationSchema),
  });

  const onSubmit = data => {
    console.log(data);
    reset();
  }

  return (
    <div className="contact-container__form">
      <p className="contact-container__form--success-message">
        {isSubmitSuccessful ? "You have successfully sent your message!" : ""}
      </p>
      <Form onSubmit={handleSubmit(onSubmit)} >
        <Form.Group className="mb-3 contact-container__form--form-group" >
          <Form.Label>First Name</Form.Label>
          <input {...register("firstName")} className="form-control" placeholder="Your first name" />
          {errors.firstName && <FormError>{errors.firstName.message}</FormError>}
        </Form.Group>
        <Form.Group className="mb-3 contact-container__form--form-group" >
          <Form.Label>Last Name</Form.Label>
          <input {...register("lastName")} className="form-control" placeholder="Your last name" />
          {errors.lastName && <FormError>{errors.lastName.message}</FormError>}
        </Form.Group>
        <Form.Group className="mb-3 contact-container__form--form-group" >
          <Form.Label>Email</Form.Label>
          <input {...register("email")} className="form-control" placeholder="Your email address" />
          {errors.email && <FormError>{errors.email.message}</FormError>}
        </Form.Group>
        <Form.Group className="mb-3 contact-container__form--form-group" >
          <Form.Label>Subject</Form.Label>
          <Form.Select {...register("subject")}>
            <option value="">Select a subject..</option>
            <option value="one">One</option>
            <option value="two">Two</option>
            <option value="three">Three</option>
          </Form.Select>
          {errors.subject && <FormError>{errors.subject.message}</FormError>}
        </Form.Group>
        <Form.Group className="mb-3 contact-container__form--form-group" >
          <Form.Label>Message</Form.Label>
          <textarea rows={6} {...register("message")} className="form-control" placeholder="Your message" />
          {errors.message && <FormError>{errors.message.message}</FormError>}
        </Form.Group>
        <button className="btn btn-primary">Send</button>
      </Form>
    </div>
  );
}
