import { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import { BASE_URL } from "../../constants/api";
import Heading from "../layout/Heading";
import Card from "react-bootstrap/Card";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import Carousel from "react-bootstrap/Carousel";
import Spinner from "react-bootstrap/Spinner";
import Alert from "react-bootstrap/Alert";



export default function DetailsPage() {
  const [product, setProduct] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [modalShow, setModalShow] = useState(false);

  let { id } = useParams();

  const url = BASE_URL + `accommodations/${id}`;

  useEffect(function () {
    async function getData() {
      try {
        const response = await axios.get(url);
        console.log("response", response);
        setProduct(response.data);
      } catch (error) {
        console.log(error);
        setError(error.toString());
      } finally {
        setLoading(false);
      }
    }

    getData();

  }, [url]);

  if (loading) return (
    <div className="d-flex justify-content-center m-5">
      <Spinner animation="border" />
    </div >
  );;

  if (error) return <Alert variant="danger">An error occurred: {error}</Alert>;

  const description = { __html: `${product.description}` };
  document.title = `Holidaze | ${product.name}`;

  function ImageModal(props) {
    return (
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
        className="details-modal"
      >
        <Modal.Header closeButton></Modal.Header>
        <Modal.Body >
          <Carousel variant="dark" className="details-carousel">
            <Carousel.Item>
              <img
                className="d-block w-100"
                src={product.images[0].url}
                alt="Second slide"
              />
            </Carousel.Item>
            <Carousel.Item>
              <img
                className="d-block w-100"
                src={product.images[1].url}
                alt="Second slide"
              />
            </Carousel.Item>
            <Carousel.Item>
              <img
                className="d-block w-100"
                src={product.images[2].url}
                alt="Third slide"
              />
            </Carousel.Item>
            <Carousel.Item>
              <img
                className="d-block w-100"
                src={product.images[3].url}
                alt="Fourth slide"
              />
            </Carousel.Item>
            <Carousel.Item>
              <img
                className="d-block w-100"
                src={product.images[4].url}
                alt="Fifth slide"
              />
            </Carousel.Item>
          </Carousel>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }



  return (
    <>
      <Heading content="Page Details" />
      <div className="breadcrumb">
        <Link to={`/accommodations`}>Back</Link>
      </div>

      <div className="details-container container" >
        <Row>
          <Col sm={6} className="details-container__image details-container__image-main" style={{ backgroundImage: `url(${product.images[0].url})` }}></Col>
          <Col sm={6} >
            <Row className="details-container__row2">
              <Col sm={6} md={3} className="details-container__image" style={{ backgroundImage: `url(${product.images[1].url})` }}></Col>
              <Col sm={6} md={3} className="details-container__image" style={{ backgroundImage: `url(${product.images[2].url})` }}></Col>

              <Col sm={6} md={3} className="details-container__image" style={{ backgroundImage: `url(${product.images[3].url})` }}></Col>
              <Col sm={6} md={3} className="details-container__image" style={{ backgroundImage: `url(${product.images[4].url})` }}></Col>
              <Col className="details-container__btn-view">
                <Button variant="primary" onClick={() => setModalShow(true)}>
                  View images
                </Button>
              </Col>
              <ImageModal
                show={modalShow}
                onHide={() => setModalShow(false)}
              />
            </Row>
          </Col>
        </Row>



        <Card.Body>
          <Card.Title>{product.name}</Card.Title>
          <Card.Text>
            Price: {product.price}
          </Card.Text>
          <div className="card-text" dangerouslySetInnerHTML={description}></div>
        </Card.Body>
      </div>
    </>
  );
}
