import PropTypes from "prop-types";
import { useState } from "react";
import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";


export default function MessageItem({ id, name, email, created_at, subject, message }) {
  const [modalShow, setModalShow] = useState(false);
  function ViewMessageModal(props) {
    return (
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            {name}
          </Modal.Title>
          <div>{email}</div>
        </Modal.Header>
        <Modal.Body>
          <h4>{subject}</h4>
          <p>{created_at}</p>
          <p>
            {message}
          </p>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }
  return (
    <Col key={id}>
      <Card>
        <Card.Body>
          <Card.Title>Sender: {name}</Card.Title>
          <Card.Text>{email}</Card.Text>
          <Card.Text>{created_at}</Card.Text>
          <Card.Text>Subject: {subject}</Card.Text>
        </Card.Body>
        <Button variant="primary" onClick={() => setModalShow(true)}>
          View message
        </Button>

        <ViewMessageModal
          show={modalShow}
          onHide={() => setModalShow(false)}
        />
      </Card>
    </Col>
  )
}

MessageItem.propTypes = {
  id: PropTypes.number.isRequired,
  name: PropTypes.string.isRequired,
  email: PropTypes.string.isRequired,
  subject: PropTypes.string.isRequired,
  message: PropTypes.string.isRequired,
};

MessageItem.defaultProps = {
  name: "Anonymous",
  subject: "Unknown"
}
