import { useState, useEffect } from "react";
import { BASE_URL, ACCOMMODATION_PATH } from "../../constants/api";
import { Link } from "react-router-dom";
import Form from "react-bootstrap/Form";

export default function SearchAccommodations() {
  const [accommodations, setAccommodations] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [display, setDisplay] = useState(true);

  const url = BASE_URL + ACCOMMODATION_PATH;

  useEffect(() => {
    async function fetchAccommodations() {
      try {
        const response = await fetch(url);
        if (response.ok) {
          const json = await response.json();
          setAccommodations(json);
        } else {
          setError("An error occurred");
        }
      } catch (error) {
        setError(error.toString());
      } finally {
        setLoading(false);
      }
    }
    fetchAccommodations();
  }, [url]);


  const handleSearch = event => {
    setSearchTerm(event.target.value);
    setDisplay(false);
  };

  const results = !searchTerm
    ? accommodations
    : accommodations.filter(accommodation =>
      accommodation.name.toLowerCase().includes(searchTerm.toLocaleLowerCase())
    );


  return (
    <>
      <Form>
        <Form.Group className="mb-3" >
          <input className="form-control" type="text" placeholder="Search for a place to stay..." value={searchTerm} onChange={handleSearch} />
        </Form.Group>
      </Form>
      <div className="search-dropdown">
        {results.map((item) => {
          return (
            <div className="search-item" key={item.id} style={{ backgroundColor: "white", display: display ? "none" : "block" }}>
              <Link className="search-item-link" to={`detail/${item.id}`}>{item.name}</Link>
            </div>
          );
        })}
      </div>
    </>
  )
}
