import hotelsImage from "../../images/category-h-christopher-jolly.jpg";
import bbImage from "../../images/category-bb-brooke-lark.jpg";
import ghImage from "../../images/category-gh-clarisse-meyer.jpg";
import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";

import { useState, useEffect } from "react";
import { BASE_URL, ACCOMMODATION_PATH } from "../../constants/api";
import Spinner from "react-bootstrap/Spinner";
import Alert from "react-bootstrap/Alert";

export default function CategoryFilter() {

  const [accommodations, setAccommodations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const url = BASE_URL + ACCOMMODATION_PATH;
  document.title = `Holidaze | Accommodations`;

  useEffect(() => {
    async function fetchAccommodations() {
      try {
        const response = await fetch(url);
        //console.log(response);
        if (response.ok) {
          const json = await response.json();
          console.log(json);
          setAccommodations(json);
        } else {
          setError("An error occurred");
        }

      } catch (error) {
        setError(error.toString());
      } finally {
        setLoading(false);
      }
    }
    fetchAccommodations();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [url]);

  if (loading) {
    return (
      <div className="d-flex justify-content-center m-5">
        <Spinner animation="border" />
      </div >
    );
  }

  if (error) {
    return <Alert variant="danger">An error occurred: {error}</Alert>;
  }

  // FILTERING CATEGORIES

  const filteredHotels = accommodations.filter(filterHotels);

  function filterHotels(hotel) {
    if (hotel.category === "hotel") {
      return true;
    }
  }
  console.log(filteredHotels);



  return (
    <Container className="category-container__links">
      <div className="category-container__links--link" style={{ backgroundImage: `url(${hotelsImage})` }}>
        <Button onClick={filterHotels}>Hotels</Button>
      </div>
      <div className="category-container__links--link" style={{ backgroundImage: `url(${bbImage})` }}>
        <Button>Bed and Breakfast</Button>
      </div>
      <div className="category-container__links--link" style={{ backgroundImage: `url(${ghImage})` }}>
        <Button>Guest Houses</Button>
      </div>
    </Container>
  );
}
